mapper.update:
        UPDATE COORDINATION_INFO
        SET TITLE=#{TITLE},
            USERNAME=#{USERNAME},
            HEIGHT=#{HEIGHT},
            WEIGHT=#{WEIGHT},
            CORD_CONTENT=#{CORD_CONTENT},
            MODIFICATION_DATE = SYSDATE(),
            JOB=#{JOB}
        WHERE COORDINATION_ID=#{COORDINATION_ID}
;
