mapper.select:
SELECT * 
FROM CIP_ATTACHFILE
WHERE SOURCE_UNIQUE_SEQ = #{SOURCE_UNIQUE_SEQ}
ORDER BY FILE_ORDER
;

SELECT * FROM CIP_ATTACHFILE
WHERE SOURCE_UNIQUE_SEQ = #{SOURCE_UNIQUE_SEQ}
GROUP BY SOURCE_UNIQUE_SEQ
;

mapper.update:
UPDATE CIP_ATTACHFILE
SET ORGINALFILE_NAME=#{ORGINALFILE_NAME}, MODIFIER_SEQ = SYSDATE()
WHERE ATTACHFILE_SEQ=#{ATTACHFILE_SEQ}
;

mapper.insert:
INSERT INTO CIP_ATTACHFILE (ATTACHFILE_SEQ, SOURCE_UNIQUE_SEQ, ORGINALFILE_NAME, PHYSICALFILE_NAME, REGISTER_SEQ, REGISTRY_DATE, MODIFIER_SEQ, MODIFY_DATE, FILE_ORDER) VALUES
#{attachFile.ATTACHFILE_SEQ}, #{attachFile.SOURCE_UNIQUE_SEQ}, #{attachFile.ORGINALFILE_NAME},#{attachFile.PHYSICALFILE_NAME}, #{attachFile.REGISTER_SEQ},SYSDATE(),#{attachFile.MODIFIER_SEQ},SYSDATE(),#{attachFile.FILE_ORDER}
;
