mapper.select:
SELECT * FROM USER A INNER JOIN QNA B WHERE A.UID = B.UID
ORDER BY DATE DESC
LIMIT #{pageBegin}, #{pageScale}

SELECT * FROM USER A INNER JOIN QNA B
WHERE A.UID = B.UID AND CATEGORY = #{CATEGORY}
ORDER BY DATE DESC;

mapper.update:
UPDATE QNA
SET STATUS='답변완료'
WHERE POST_NO_QNA=#{SOURCE_UNIQUE_SEQ}

UPDATE QNA
SET STATUS='답변대기'
WHERE POST_NO_QNA=#{SOURCE_UNIQUE_SEQ}

mapper.select:
SELECT * FROM USER A INNER JOIN FAQ B
WHERE A.UID = B.UID
ORDER BY DATE DESC;

SELECT * FROM USER A INNER JOIN FAQ B
WHERE A.UID = B.UID AND CATEGORY = #{CATEGORY}
ORDER BY DATE DESC;

mapper.update:
UPDATE FAQ
SET TITLE=#{TITLE},
    CATEGORY=#{CATEGORY},
    DATE=DATE_FORMAT(now(),'%Y.%m.%d'),
    CONTENT=#{CONTENT}
WHERE POST_NO_FAQ=#{POST_NO_FAQ}

mapper.select:
SELECT * FROM ANNOUNCEMENT
WHERE POST_NO_ANNO = #{POST_NO_ANNO}
ORDER BY DATE DESC;

SELECT * FROM USER A INNER JOIN ANNOUNCEMENT B
WHERE A.UID = B.UID
ORDER BY DATE DESC;

SELECT * FROM USER A INNER JOIN ANNOUNCEMENT B
WHERE A.UID = B.UID AND CATEGORY = #{CATEGORY}
ORDER BY DATE DESC;

mapper.update:
UPDATE ANNOUNCEMENT
SET TITLE=#{TITLE},
    CATEGORY=#{CATEGORY},
    DATE=DATE_FORMAT(now(),'%Y.%m.%d'),
    CONTENT=#{CONTENT}
WHERE POST_NO_ANNO=#{POST_NO_ANNO}
