mapper.select:
SELECT * FROM PRODUCT JOIN (SELECT * FROM CIP_ATTACHFILE GROUP BY SOURCE_UNIQUE_SEQ) AS CIP ON PRODUCT_ID = SOURCE_UNIQUE_SEQ
WHERE CATEGORY LIKE "여자%"
;

mapper.insert:
INSERT INTO PRODUCT
(PRODUCT_ID, PICTURE, BRAND, CLOTHES_NAME, PRICE, IS_BESTSELLING_PRODUCT)
VALUES(#{PRODUCT_ID},#{PICTURE},#{BRAND},#{CLOTHES_NAME},#{PRICE},#{IS_BESTSELLING_PRODUCT});

mapper.update:
UPDATE PRODUCT
SET PRICE = #{PRICE}
WHERE PRODUCT_ID=#{PRODUCT_ID};

UPDATE PRODUCT
SET SALES_VOLUME = #{SALES_VOLUME}
WHERE PRODUCT_ID = #{PRODUCT_ID};
